configuration.js:

    $(document).ready(function() {
        // Ensure all images are visible
        $('img').each(function() {
            $(this).css({
                'max-width': '100%',
                'height': 'auto',
                'display': 'block'
            });
        });
        
        // Add click to enlarge functionality
        $('img').click(function() {
            var src = $(this).attr('src');
            var img = '<img src="' + src + '" style="max-width:90%; max-height:90%;" />';
            var modal = '<div style="position:fixed; top:0; left:0; width:100%; height:100%; background:rgba(0,0,0,0.9); z-index:9999; display:flex; align-items:center; justify-content:center;" onclick="$(this).remove();">' + img + '</div>';
            $('body').append(modal);
        });
    });
