configuration.js:
$(document).ready(function() {
    $('img').css({
        'max-width': '100%',
        'height': 'auto',
        'display': 'block'
    });
    $('img').click(function() {
        var src = $(this).attr('src');
        var modal = '<div style="position:fixed; top:0; left:0; width:100%; height:100%; background:rgba(0,0,0,0.9); z-index:9999; display:flex; align-items:center; justify-content:center;" onclick="$(this).remove();"><img src="' + src + '" style="max-width:90%; max-height:90%;"/></div>';
        $('body').append(modal);
    });
});
